	<script type="text/javascript" language="javascript">//Function called from home page flash booking mask.
	function popup(url,swfId) 
	{
	 var winName = Math.round(9999*Math.random()) + new Date().getTime();
	 var width  = screen.width;
	 var height = screen.height;
	 var left   = (screen.width  - width)/2;
	 var top    = (screen.height - height)/2;
	 
	 var params = 'width='+width+', height='+height;
	 params += ', top='+top+', left='+left;
	 params += ', directories=no';
	 params += ', location=yes';
	 params += ', menubar=yes';
	 params += ', resizable=yes';
	 params += ', scrollbars=yes';
	 params += ', status=no';
	 params += ', toolbar=no';
	 
        var winNew = window.open(url,winName, params);

        if(!winNew) {
			
            getSwf(swfId).openWindowFromSwf(url);
        }
        else {
            winNew.focus();
        }
	 
	}
	//Function used by flash booking mask links
	function getSwf(id) {
	
        if (navigator.appName.indexOf("Microsoft") != -1) {
		
            return window[id];
        } 
        else {
		
            return document[id];
			
        }
    } </script>
	

